        }
        return this.state.todos.map(
            function(currentTodo, i){
            return <Todo todo={currentTodo} deleteItem={this.deleteItemHandler} key={i}   />;
        })
    }

    render() {
        const oncl = this.ontodoDelete;
        return (
            <div>
            <h3>Todos List</h3>
            <table className="table table-striped" style={{ marginTop: 20 }} >
                <thead>
                    <tr>
                        <th>Description</th>
                        <th>Responsible</th>
                        <th>Priority</th>
                        <th>Action</th>
                    </tr>
                </thead>
                <tbody>

                    { this.todoList(oncl) }

                </tbody>
            </table>
        </div>
        )
    }
}
