import React, { Component } from 'react';
import {Link} from 'react-router-dom';
import axios from 'axios';
const Todo = (props) => (


    <tr>
        <td className={props.todo.todo_completed ? 'completed' : ''}>{props.todo.todo_description}</td>
        <td className={props.todo.todo_completed ? 'completed' : ''}>{props.todo.todo_responsible}</td>
        <td className={props.todo.todo_completed ? 'completed' : ''}>{props.todo.todo_priority}</td>
        <td>
            <Link to={"/edit/"+props.todo._id}>Edit</Link>
            <br />
            <OnDelete onClick={props.todo._id, props.listimes} />

        </td>
    </tr>
)
const onClick = (arg, list) => {

axios.delete('http://localhost:4000/todos/'+arg._id)
        .then(response => {

           // alert("Todo Has been deleted");
           let todoListCopy = this.state.todos;
           console.log("<<<<START>>>>");
           console.log(this);
           console.log("<<<<END>>>>");
        })
        .catch(function (error){
            console.log(error);
        })
}
const OnDelete = (arg) =>{
    console.log("ddddd");
    console.log(tdlist);
    console.log("<<<>>>d");
    return <button onClick={()=>onClick(arg.onClick)} className="delete-btn">Delete</button>
}
export default class TodosList extends Component {
    constructor(props) {
        super(props);
        this.todoList = this.todoList.bind(this);
        this.componentDidMount=this.componentDidMount.bind(this);
        this.state = {todos: []};
    }

    componentDidMount() {
        axios.get('http://localhost:4000/todos/')
            .then(response => {
                this.setState({ todos: response.data });
            })
            .catch(function (error){
                console.log(error);
            })
    }

    todoList() {

        return this.state.todos.map(
            function(currentTodo, i){

            return <Todo todo={currentTodo} key={i} listimes={this}  />;
        })
    }

    render() {
        return (
            <div>
            <h3>Todos List</h3>
            <table className="table table-striped" style={{ marginTop: 20 }} >
                <thead>
                    <tr>
                        <th>Description</th>
                        <th>Responsible</th>
                        <th>Priority</th>
                        <th>Action</th>
                    </tr>
                </thead>
                <tbody>
                    { this.todoList() }
                </tbody>
            </table>
        </div>
        )
    }
}
