import React, { Component } from 'react';
import {Link} from 'react-router-dom';
import axios from 'axios';
const Todo = (props) => (


    <tr>
        <td className={props.todo.todo_completed ? 'completed' : ''}>{props.todo.todo_description}</td>
        <td className={props.todo.todo_completed ? 'completed' : ''}>{props.todo.todo_responsible}</td>
        <td className={props.todo.todo_completed ? 'completed' : ''}>{props.todo.todo_priority}</td>
        <td>
            <Link to={"/edit/"+props.todo._id}>Edit</Link>
            <br />

            <button onClick={props.onDelete} className="delete-btn">Delete</button>

        </td>
    </tr>
)
const onClick = (arg) => {
    axios.delete('http://localhost:4000/todos/'+arg.id)
        .then(response => {

           // alert("Todo Has been deleted");

        })
        .catch(function (error){
            console.log(error);
        })

}
const OnDelete = (arg, todoLitem) =>{
    console.log("ddddd");
    console.log(todoLitem);
    console.log("<<<>>>d");
    return <button onClick={()=>onClick(arg)} className="delete-btn">Delete</button>
}

export default class TodosList extends Component {

    constructor(props) {
        super(props);

        this.todoList = this.todoList.bind(this);
        this.onDelete = this.onDelete.bind(this);
        this.componentDidMount=this.componentDidMount.bind(this);
        this.state = {todos: []};
    }

    componentDidMount = () => {
        axios.get('http://localhost:4000/todos/')
            .then(response => {
                this.setState({ todos: response.data });
            })
            .catch(function (error){
                console.log(error);
            })
    }
   onDelete = () => {
       console.log("fffff");
    // axios.delete('http://localhost:4000/todos/'+arg.id)
    // .then(response => {

    //    // alert("Todo Has been deleted");

    // })
    // .catch(function (error){
    //     console.log(error);
    // })
   }
    todoList = () => {

        return this.state.todos.map(
            function(currentTodo, i){

            return <Todo todo={currentTodo} onDelete={this.onDelete}  />;
        })
    }

    render() {
        return (
            <div>
            <h3>Todos List</h3>
            <table className="table table-striped" style={{ marginTop: 20 }} >
                <thead>
                    <tr>
                        <th>Description</th>
                        <th>Responsible</th>
                        <th>Priority</th>
                        <th>Action</th>
                    </tr>
                </thead>
                <tbody>
                    { this.todoList() }
                </tbody>
            </table>
        </div>
        )
    }
}
