import React, { Component } from 'react';
import {Link} from 'react-router-dom';
import axios from 'axios';
const Todo = props => (
    <tr>
        <td className={props.todo.todo_completed ? 'completed' : ''}>{props.todo.todo_description}</td>
        <td className={props.todo.todo_completed ? 'completed' : ''}>{props.todo.todo_responsible}</td>
        <td className={props.todo.todo_completed ? 'completed' : ''}>{props.todo.todo_priority}</td>
        <td>
            <Link to={"/edit/"+props.todo._id}>Edit</Link>
            <br />
            <OnDelete todoid={props.todo._id} />

        </td>
    </tr>
)
const OnDelete = arg =>{
    console.log(arg);
    let link = <Link to={"/todos/"+arg.todoid} />
    //console.log(this.props);
    return <button className="delete-btn">Delete</button>
}
export default class TodosList extends Component {
    constructor(props) {
        super(props);
        this.state = {todos: []};
    }
    _onDelete(props){
        axios.get('http://localhost:4000/todos/'+props.args_id)
        .then(response => {
            this.setState({ todos: response.data });
        })
        .catch(function (error){
            console.log(error);
        })
    }
    componentDidMount() {
        axios.get('http://localhost:4000/todos/')
            .then(response => {
                this.setState({ todos: response.data });
            })
            .catch(function (error){
                console.log(error);
            })
    }

    todoList() {
        console.log(this.state);
        return this.state.todos.map(
            function(currentTodo, i){

            return <Todo todo={currentTodo} key={i}  />;
        })
    }

    render() {
        return (
            <div>
            <h3>Todos List</h3>
            <table className="table table-striped" style={{ marginTop: 20 }} >
                <thead>
                    <tr>
                        <th>Description</th>
                        <th>Responsible</th>
                        <th>Priority</th>
                        <th>Action</th>
                    </tr>
                </thead>
                <tbody>
                    { this.todoList() }
                </tbody>
            </table>
        </div>
        )
    }
}
